mapper.select:
select t1.id firstid ,t1.testproject firstname ,t2.id secondid ,t2.testproject secondname ,t.id ,t.companyid ,t.testprojectid ,t.methodid ,t.standardid ,t.projectsort ,t3.testproject ,tm.methodname ,tstand.standardname ,t.methodsort ,t.limitcomment ,t.price from t_qualification t inner join ( select id,pid,testproject from (
select t1.id,t1.pid,t1.level,t1.testproject,
if(find_in_set(pid, @pids) > 0, @pids := concat(@pids, ',', id), 0) as ischild
from (
select id,pid,level,testproject from t_test_category t order by pid, id
) t1,
(select @pids := #{id} ) t2
) t3 where ischild != 0 and level=3 union select id,pid,testproject from t_test_category where level=3 and id = #{id} )t3 on t3.id=t.testprojectid inner join t_test_category t2 on t2.id=t3.pid inner join t_test_category t1 on t1.id=t2.pid left join t_test_method tm on tm.id=t.methodid left join t_test_standard tstand on tstand.id=t.standardid where t.companyid=1 order by t.projectsort,t.methodsort

select t1.id firstid ,t1.testproject firstname ,t2.id secondid ,t2.testproject secondname ,t.id ,t.companyid ,t.testprojectid ,t.methodid ,t.standardid ,t.projectsort ,t3.testproject ,tm.methodname ,tstand.standardname ,t.methodsort ,t.limitcomment ,t.price from t_qualification t inner join ( select id,pid,testproject from (
select t1.id,t1.pid,t1.level,t1.testproject,
if(find_in_set(pid, @pids) > 0, @pids := concat(@pids, ',', id), 0) as ischild
from (
select id,pid,level,testproject from t_test_category t order by pid, id
) t1,
(select @pids := #{id} ) t2
) t3 where ischild != 0 and level=3 union select id,pid,testproject from t_test_category where level=3 and id = #{id} )t3 on t3.id=t.testprojectid inner join t_test_category t2 on t2.id=t3.pid inner join t_test_category t1 on t1.id=t2.pid left join t_test_method tm on tm.id=t.methodid left join t_test_standard tstand on tstand.id=t.standardid where t.companyid=#{companyid} order by t.projectsort,t.methodsort

mapper.update:
update t_qualification
set
   testprojectid=#{testprojectid}, 
   standardid=#{standardid},
  methodid=#{methodid}
  ,price=#{price}
  where id=#{id}

mapper.insert:
insert into t_qualification
(

  testprojectid,
  companyid,
  standardid,
  methodid
  ,price
 )
 values(
 #{testprojectid}
 ,#{companyid}
 ,#{standardid}
 ,#{methodid}
 ,#{price}
 )
